mapper.select:
SELECT id, name, thumb_url, parent_id, created_at
FROM category
WHERE id = #{id}

SELECT parent.id AS id, parent.name AS name, parent.thumb_url AS thumbUrl, parent.parent_id AS parentId, parent.created_at AS createdAt, IFNULL(p.post_count, 0) AS postCount FROM category parent LEFT JOIN (SELECT category_id, COUNT(*) AS post_count FROM post GROUP BY category_id) p ON parent.id = p.category_id WHERE parent.id = #{parentId}
UNION ALL
SELECT children.id AS id, children.name AS name, children.thumb_url AS thumbUrl, children.parent_id AS parentId, children.created_at AS createdAt, IFNULL(p.post_count, 0) AS postCount FROM category children LEFT JOIN (SELECT category_id, COUNT(*) AS post_count FROM post GROUP BY category_id) p ON children.id = p.category_id WHERE children.parent_id = #{parentId}
